script:
return (function (elem) {
        return (function() {
      // element has a shadowRoot property
      if (this.shadowRoot) {
        return this.shadowRoot.querySelector('//a[text()="Shop Now"]')
      }
      // fall back to querying the element directly if not
      return this.querySelector('//a[text()="Shop Now"]')
    }).call(elem);
    }).apply(null, arguments)
